member.summary:
            시간 지연을 위한 함수

사용 방법

void Do()

{

	StartCoroutine(Move("object"));

}

IEnumerator Move(GameObject target)

{

    yield return target.MoveTo(gameObject.transform.position, new Vector3(0, 2,/ 0), 1.0f, null, fniTransitionType.EaseInOutSine);

    yield return Delay(2.0f);

    yield return target.MoveTo(gameObject.transform.position, new Vector3(4, 0,/ 0), 1.0f, null, fniTransitionType.EaseInOutSine);

}

